'use strict';

var Node = require('./node');

/**
 * Initialize a `Blockreview` with the given `block`.
 *
 * @param {String} val
 * @param {Block} block
 * @param {Boolean} buffer
 * @api public
 */

var Blockreview = module.exports = function Blockreview(val, block, buffer) {
  this.block = block;
  this.val = val;
  this.buffer = buffer;
};

// Inherit from `Node`.
Blockreview.prototype = Object.create(Node.prototype);
Blockreview.prototype.constructor = Blockreview;

Blockreview.prototype.type = 'Blockreview';
