'use strict';

var Node = require('./node');

/**
 * Initialize a `review` with the given `val`, optionally `buffer`,
 * otherwise the review may render in the output.
 *
 * @param {String} val
 * @param {Boolean} buffer
 * @api public
 */

var review = module.exports = function review(val, buffer) {
  this.val = val;
  this.buffer = buffer;
};

// Inherit from `Node`.
review.prototype = Object.create(Node.prototype);
review.prototype.constructor = review;

review.prototype.type = 'review';
